-- title:  Perfect Day Story
-- author: hejiabin
-- desc:   a simple RPG
-- script: lua

stat={title=0,game=1,result=2,trans=-1}
emap={pass=0,block=1,indoor=2}
ingame={move=0,talk=1}

gstat=stat.title
gtick=0

gingame=ingame.move

role=
{
	x=50,
	y=50,
	wid=16,
	hei=24,
	speed=1,
	centerx=115,
	centery=55
}

gmap=
{
 wid=240,
	hei=136
}

function TIC()
	if gstat==stat.title then
		stat_title()
	elseif gstat==stat.game then
	 stat_game()
	elseif gstat==stat.result then
	 --TODO
	end
	gtick=gtick+1
end

function stat_title()
	cls(4)
	spr(10,0,0,0,2,0,0)
	spr(10,240-16,0,0,2,1,0)
	spr(10,0,136-16,0,2,2,0)
	spr(10,240-16,136-16,0,2,3,0)
	for i=0,12 do
		spr(11,(i+1)*16,0,0,2,0,0)
		spr(11,(i+1)*16,136-16,0,2,2,0)
	end
	for i=0,5 do
		spr(11,0,(i+1)*16,0,2,0,3)
		spr(11,240-16,(i+1)*16,0,2,1,3)
	end
	spr(11,0,104,0,2,0,3)
	spr(11,240-16,104,0,2,1,3)
	
	spr(0,24,30,0,2,0,0,2,2)
	spr(2,64,30,0,2,0,0,2,2)
	spr(4,104,30,0,2,0,0,2,2)
	spr(6,144,30,0,2,0,0,2,2)
	spr(8,184,30,0,2,0,0,2,2)
	print("2030",72,70,2,true,4)
	
	print("Press A to start", 72,110,1)
	
 if btnp(4,60,6) then
		gstat=1
		trace("enter game")
	end
end

function stat_game()
	if gingame==ingame.move then
 	role:updateMove()

 	cls(4)
		gmap:draw()
		role:draw()
	elseif gingame==ingame.talk then
	 --TODO
	end
end

function gmap.draw(self)
 local x=role.x-role.centerx
	local y=role.y-role.centery
	local dx=x%8
	local dy=y%8
	
	if x<0 then
	 x=0
		dx=0
	end
	if y<0 then
	 y=0
		dy=0
	end
	--max
	--TODO
	
 map(x//8,y//8,31,18,-dx,-dy)
end

function role.draw(self)
 local	frame=256+((gtick//40)%2)*2
 local	x=self.centerx
 local	y=self.centery
	--adjust pos
	if self.x<self.centerx then
	 x=self.x
	end
	if self.y<self.centery then
	 y=self.y
	end
	--max
	--TODO
	
	spr(frame,x,y,0,1,0,0,2,3)
end

function role.updateMove(self)
 local newx=self.x
	local newy=self.y
 if btn(0) then	--up
	 newy=newy-self.speed
	end
	if btn(1) then --down
	 newy=newy+self.speed
	end
	if btn(2) then --left
	 newx=newx-self.speed
	end
	if btn(3) then --right
	 newx=newx+self.speed
	end
	
	--block check
	local offsetx={0,self.wid-1,0,self.wid-1}
	local offsety={0,0,self.hei-1,self.hei-1}
	for i=1,4 do
	 local mapx=(newx+offsetx[i])//8
		local mapy=(newy+offsety[i])//8
	 local tile=mget(mapx,mapy)
		if fget(tile,emap.block) then
		 return
		end
	end
	
	self.x=newx
	self.y=newy
end

function show_text(txt)
 --show text
end
